interface MenuItemProps {
  icon: ReactNode;
  label: string;
  isCollapsable?: boolean;
}
const MenuItem: React.FC<MenuItemProps> = ({
  icon,
  label,
  isCollapsable = false,
}) => {
  return (
    <div className="mt-2.5 text-base h-10 px-2.5 flex items-center">
      {icon}
      <div className="ml-3">{label}</div>
      {isCollapsable && (
        <div className="ml-auto">
          <DownIcon />
        </div>
      )}
    </div>
  );
};

const Menu = () => {
  return (
    <div className="w-[280px] mr-[30px] mt-0.5 text-color1">
      <div className="mb-[10px] flex items-center">
        <img
          className="rounded-full"
          src="/profile.png"
          alt="profile"
        />
        <div className="ml-[15px]">
          <div className="text-[18px] font-semibold">Sven Pietsch</div>
          <div className="text-sm">Innoloft GmbH</div>
        </div>
      </div>
      <MenuItem
        icon={<HomeIcon />}
        label="Home"
      />
      <MenuItem
        icon={<MembersIcon />}
        label="Members"
      />
      <MenuItem
        icon={<OrganizationsIcon />}
        label="Organizations"
        isCollapsable={true}
      />
    </div>
  );
};

export default Menu;
